let t=document.createElement("style");t.innerHTML=".el-table{text-align:center;margin:0 auto}.el-collapse{white-space:pre-wrap;text-align:center;margin:0 auto;width:80%}.el-collapse .el-collapse-item{text-align:left}.contentsImg{max-width:100%;max-height:500px;display:block;margin:auto}.tablesStyle{text-align:center}.el-table .cell{white-space:pre-line}.subtitle{font-size:x-large;text-align:center}.capterL1{font-size:larger;text-align:center}.capterL2{font-size:medium;text-align:left}.capterL3{white-space:pre-wrap;font-size:small;text-align:left}",document.head.appendChild(t);import{d as e,k as n,o as l,f as o,b as a,i as r,e as c,w as s,F as i,r as u,h as p}from"./index.d1e7243e.js";const m={class:"subtitle"},d=a("br",null,null,-1),f=a("br",null,null,-1),y=a("br",null,null,-1),b=a("br",null,null,-1),h=a("br",null,null,-1),g=a("br",null,null,-1);var x=e({__name:"contents-3.1.2",setup(t){const e=n("auto"),x=n("电气注意"),w=n("电气全般"),S=[{from:"供给侧电源",content:"・誤って操作されると重大事故となるスイッチは意識的に操作しない プッシュできないようにする。"},{from:"开关",content:"・電源投入を示す表示灯を制御盤に取り付けること。・制御電源の通電状態を示す表示灯を設けること。\n・装置の稼働状態を示す 5 色積層表示灯を設け、異常時はブザー発鳴を行うこと。"},{from:"电器柜",content:"・保守点検が容易な部品、器具の配置をすること。\n・制御盤等の空気温度は、数箇所測定した最大値が、50℃以下とする。\n・装置メンテ用に接地付きコンセント 2 口(ダブルコンセント)を制御盤内に取付のこと。\n・装置メンテ用コンセントの電気容量は 5A 以上のこと。\n・配線過熱、火災を早期に発見するため、温度検出器（サーモスタット等）又は小型煙感知器を設置するか、配線ダクト内に電線温度異常検出器（温度検知線等）を設置するか、3 つの手段の内適切なものを 1 つ以上実施すること。\n・爆発する危険のあるガス、蒸気のそばに設置しないこと。 （設置する時は防爆構造のこと)\n・紫外線、薬品類で腐食されないように対策すること。 （侵入防止、設置場所）\n・極力、ノイズ対策として制御用電源とヒーター等の電源は系統を分けること。"},{from:"配线",content:"・被覆電線にキズ、ヤブレがないこと。また、リード線は端子から端子まで接続無しで配線すること。（ツギ配線・ハダカ電線の禁止）\n・移動電線は、可撓性を考慮し専用のケーブル（ブレークレスケーブル）を用いると共に\n極力交換の容易な構造とする。ケーブルベアの容量は、線材の 2 倍程度とすること。\n（容量比 50～60%）\n・配線ダクトへの配線容量は、60%以下とする。\n・ケーブルダクト等配線材料は、難 燃性材 料 を使用すること。\n・制御回路は、DC24V 以下とすること。\n・電気配線をネジで固定する場合は、固定ネジ緩みがないことを確認し、マーキングすること。"},{from:"静电防止",content:"・人体が触れると感電する恐れのある充電部の露出がないこと。露出充電部は、カバーを設けること。 （特に制御盤以外）\n・メンテナンス等で、体が誤って触れて通電状態となり、感電する恐れのあるスイッチには、カバー等を設けること。又は簡単に ON しない構造のスイッチを使用すること。\n・AC600V 又は DC750V を越える部分については「高電圧注意」の表示をすると共に露出しないようにすること。\n・機械装置、制御外箱及び制御部の接地を行うこと。本体金属部は、全て同電位とすること。\n・装置本体と付帯機器（ポンプ、電源ボックス等）を離して接する可能性がある場合は、それぞれの機器に電\n源 ON/OFF スイッチを設けること。\n（付帯機器メンテナンス時の不用意電源 ON 防止）"},{from:"紧急停止",content:"・非常停止スイッチを操作パネルに設ける。また、必要に応じて装置側にも取り付けること。長い設備には、要所毎に設けること。\n・非常停止は、本体及び付属装置を全停止させることを原則とする。プロセス安全上から部分停止の場合は、その旨表示のこと。\n・非常停止スイッチは赤色キノコ形でロックできる構造のこと。\n・非常停止時、停電時にはその場で停止し、かつ、電源復帰時にそのまま再起動しないこと。"},{from:"按压开关",content:"・誤って操作されると重大事故となるスイッチは意識的に操作しない プッシュできないようにする。"},{from:"表示灯",content:"・電源投入を示す表示灯を制御盤に取り付けること。\n・制御電源の通電状態を示す表示灯を設けること。\n・装置の稼働状態を示す 5 色積層表示灯を設け、異常時はブザー発鳴を行うこと。"},{from:"安全回路",content:"・非常停止や停電によりクランプしているワークを解放しないこと。\n・非常停止や停電後は、人手による復帰をしない限り、再起動しないこと。\n・ロボット、搬送車はサービス領域内の人、障害物の検出機構を備え、危険状態では、自動で非常停止すること。サービス時は警報音、光を発すること。非常時は誰でも操作できる非常スイッチを移動部、地上部双方の見やすい位置につけること。\n・ロボットの腕、プレスのラム等において現位置を保持するための無励磁式ブレーキを必要個所に設けること。\n・NC 位置決め方式等で、あらかじめ、入力設定された動作範囲を超えて移動したとき、停止すること。（ソフトエラー域の設置）また、ある範囲を超えて移動したときは、リミットスイッチ等で検知し、停止すること。（ハード的オーバーラン用）\n・トラブル発生時は、原因部分の動作を停止させると共に積層表示灯で知らせること。"},{from:"安全启动",content:"・プレス装置等の類似装置では両手にて押しボタンを同時に押すことにより作動すること。\n・プレス装置等の類似の装置では装置前面に光電スイッチ等の安全検出器を設けること。また、扉にはリミットスイッチ等を設け開口時は起動しない回路とすること。\n・起動、駆動への信号となるリミットスイッチは、装置に内蔵もしくはカバーを付け、人・物が不用意に触れないようにすること。"},{from:"保护回路",content:"・回路ショート、過電流に対し、十分な機能を果たす保護装置を設けること。\n・回生抵抗等の発熱する制御回路には、温度検出回路を設け過昇温を防止すること。\n・電動機 0.1kW 以上の負荷には、サーマルリレー等による保護装置を設けること。\n・ヒーターを使用する場合は、過昇温防止回路を設けて、異常時は、自動的に電路を OFF させ、安全性を図ること。（詳細は⑯項に示す）"},{from:"接地",content:"・機械装置、制御箱、制御部は接地すること。\n・静電気の発生により、火災等の危険のある薬品及びガスを使用する装置には、確実な接地をすること。（電気使用の有無に係わらず）"},{from:"电噪声对策",content:"・ノイズ耐量は､コモン･ノーマル両モード共にノイズ電圧 1000V,1μs ノイズシミュレータによる。"},{from:"瞬低对策",content:"・電圧 0V、電源周波数 1Hz 以内の瞬低において、正常に動作を継続できるか完全に停止できること。この時間を越えた場合でも異常動作による危険状態が発生しないこと。\n・PC の電源部には UPS を設置すること。また、OS 自動シャットダウン機能付きのものを使用すること。"},{from:"防爆对策",content:"・可燃性ガスや薬品を使用する装置の電気機器は、火災爆発を起こさないように、その使用場所により、安全性防爆機器及び防爆機器等を使用し、対策をとること。"},{from:"过升温对策",content:"・ホットプレートやヒーターは熱暴走や故障対策として下記を備えること。またその機能を出荷前に必ずチェックし成績書を添付のこと。ヒーターに接する部位に使用する樹脂部品や配線、塗装等は過昇温の設定温度＋10℃まで耐える材料を使用のこと。下記(1)(3)(4)(5)はPLC を使用する機器は、エラーの履歴を保存する機能を有すること。\n(1)使用温度最大値に応じた過昇温設定値が入力でき、動作した場合には暴走しないことを確認すること。\n(2)過昇温設定が動作しない場合を想定して、サーモスタットを備え、ヒーター電源がカットされ暴走しない対策をとること。\n(3)コントロール用熱電対のオープンショート時には保護機能が動作し、ヒーター電源をカットすること。\n(4)ヒーターが 1 つの温度コントローラに複数回路ある場合は 1 つが断線、ショートしても検知し停止すること。\n(5)SSRを使用する場合は、故障したときに暴走しないようSSR故障を検知して電源を遮断する機能を有すること。\n(6)ヒーター断線対策として、ヒーター体（コアヒーター等）とヒーターブロック間の熱抵抗を下げるよう高熱伝導グリス等を塗布し、昇温時にヒーター体の表面の最高温度が、温度コントローラ設定温度+100℃以下になるように施工すること。（アレニウスの式 10℃2 倍則を考慮し、ヒーター体の最大温度上昇を抑えること）"}],C=n("阻燃性线束标准"),H=[{from:"易燃性",content:"燃焼性については、JIS C3005 に規定されている傾斜燃焼試験法において、60 秒以内に自然に消火するものであること(自己消炎性)。但し、JIS において難燃性が規定されているものについては、その規格も同時に満足すること。"},{from:"发烟浓度",content:"発煙濃度については、米国基準局（National Bureau of Standard 略称;NBS）の発煙濃度試験法（AmericanSociety for Testing and Materials 略号;ASTM の規格；E662）により測定された濃度が 400 以下のもの。"},{from:"卤化氢挥发量",content:"ハロゲン化水素発生量については、ハロゲン化水素発生量（フッ化水素を除く）が、350mg/g 以下で、かつ、フッ化水素発生量が 200mg/g 以下のもの。"}],_=n(""),v=[{type:"交直流回路",current:"三相",sign:"第一相",colorE:"R",colorH:"红",colorC:"红",other:"/"},{type:"交直流回路",current:"三相",sign:"第二相",colorE:"S",colorH:"白",colorC:"白",other:"/"},{type:"交直流回路",current:"三相",sign:"第三相",colorE:"T",colorH:"黑",colorC:"黑",other:"/"},{type:"交直流回路",current:"三相",sign:"接地",colorE:"E",colorH:"绿（红）",colorC:"绿",other:"/"},{type:"交流控制回路",colorH:"绿（红）",colorC:"绿",other:"/"},{type:"直流控制回路",colorH:"蓝",colorC:"蓝",other:"/"},{type:"接地回路",colorH:"绿",colorC:"/",other:"/"},{type:"外部回路",colorH:"绿",colorC:"/",other:"/"}];return(t,n)=>{const T=u("el-tag"),E=u("el-table-column"),N=u("el-table"),O=u("el-backtop");return l(),o(i,null,[a("h2",m,"3.1.2   "+r(x.value),1),a("h3",null,r(w.value),1),c(T,null,{default:s((()=>[p("Tag 1")])),_:1}),c(T,{class:"ml-2",type:"success"},{default:s((()=>[p("Tag 2")])),_:1}),c(T,{class:"ml-2",type:"success"},{default:s((()=>[p("Tag 2")])),_:1}),c(T,{class:"ml-2",type:"success"},{default:s((()=>[p("Tag 2")])),_:1}),c(N,{class:"tablesStyle",data:S,stripe:"",style:{width:"80%"},"table-layout":e.value},{default:s((()=>[c(E,{type:"selection",width:"55"}),c(E,{type:"index",index:1}),c(E,{prop:"from",label:"各种动力源",width:"120"}),c(E,{prop:"content",label:"内容"})])),_:1},8,["table-layout"]),d,f,y,a("h3",null,r(C.value),1),c(N,{class:"tablesStyle",data:H,stripe:"",style:{width:"80%"},"table-layout":e.value},{default:s((()=>[c(E,{type:"selection",width:"55"}),c(E,{type:"index",index:1}),c(E,{prop:"from",label:"项目",width:"120"}),c(E,{prop:"content",label:"内容"})])),_:1},8,["table-layout"]),b,h,g,a("h3",null,r(_.value),1),c(N,{class:"tablesStyle",data:v,stripe:"",style:{width:"80%"},"table-layout":e.value},{default:s((()=>[c(E,{type:"selection",width:"55"}),c(E,{type:"index",index:1}),c(E,{prop:"type",label:"种类",width:"120"}),c(E,{prop:"content",label:"内容"})])),_:1},8,["table-layout"]),c(O,{right:100,bottom:100})],64)}}});export{x as default};
